import React from 'react'
import {useNavigate} from "react-router-dom"

function ItemDetail({itemView}){
    const {title, backdrop_path, vote_average, vote_count, release_date, overview}= itemView;
    ///back button
    const imgPath= "https://image.tmdb.org/t/p/original" + backdrop_path;
    return (
        <div className='item_details'>
        { itemView && (
            <div>
                <h1 className='title'>{title}</h1>
                <div className='other_details'>
                    <ul>
                        <li>Movie Description : <span>{overview}</span></li>
                        <li> Avg. Vote : <span>{vote_average}</span></li>
                        <li>Vote Count : <span>{vote_count}</span></li>
                        <li>Release Date : <span>{release_date}</span></li>
                    </ul>
                </div>

            </div>
        )
        }
        </div>

    )
}



export default ItemDetail
